mapper.select:
select a.articelId,u.UserName,c.title,a.name,a.userTel,a.userAddress,a.comAddress,b.timeArea,a.statue,a.orderDate from userorder a
left join user u on a.userId=u.id
left join artical c on a.articelId=c.id
left join timearea b on a.timeArea=b.id
where 1=1 and a.articelId=#{condarticel.id}
limit #{begin},#{end}
